    }

    .tmpl_hh_home_video {
        margin: 0 25px 60px;
        border-radius: 20px;
    }

    .tmpl_hh_video {
        padding-bottom: 72.2%;
    }

    .tmpl_hh_video .tmpl_hh_home_video__play {
        width: 34px;
        height: 34px;
    }
}

